#include "pch.h"/*** @file  Test.cpp*///#include "EnglishRussianDictionary.h"#include "EnglishRussianDictionary.cpp"TEST(DictionaryTest, TranslationTest) {   EnglishRussianDictionary dictionary;	dictionary.AddNewPairOfWords("Table", "Стол");	ASSERT_EQ(dictionary["Table"], "Стол");}TEST(DictionaryTest, RemoveElementTest) {	EnglishRussianDictionary dictionary;	dictionary.AddNewPairOfWords("BSUIR", "БГУИР");	ASSERT_TRUE(dictionary.("BSUIR"));	dictionary.DeleteEnglishWord("BSUIR");	ASSERT_FALSE(dictionary.IsContains("BSUIR"));}TEST(DictionaryTest, UpdateTranslationTest) {	EnglishRussianDictionary dictionary;	dictionary.AddNewPairOfWords("ai", "ии");	dictionary.AddNewPairOfWords("reading", "чтение");	dictionary.ChangeTranslation("ai", "топспецуха");	dictionary.ChangeTranslation("reading", "хобби");	ASSERT_EQ(dictionary["ai"], "топспецуха");	ASSERT_EQ(dictionary["reading"], "хобби");}TEST(DictionaryTest, GetCountTest) {	EnglishRussianDictionary dictionary;	dictionary.AddNewPairOfWords("sun", "солнце");	dictionary.AddNewPairOfWords("shiba-inu", "сиба-ину");	dictionary.AddNewPairOfWords("mint", "мята");	dictionary.AddNewPairOfWords("beauty", "красота");	dictionary.AddNewPairOfWords("japan", "япония");	dictionary.AddNewPairOfWords("clover", "клевер");	dictionary.AddNewPairOfWords("lavender", "лаванда");	dictionary.AddNewPairOfWords("design", "дизайн");	dictionary.AddNewPairOfWords("headphones", "наушники");	dictionary.AddNewPairOfWords("library", "библиотека");	dictionary.AddNewPairOfWords("outfit", "образ");	ASSERT_EQ(dictionary.CountNamberOfWords(dictionary), 11);}TEST(DictionaryTest, LoadFromFileTest) {	EnglishRussianDictionary dictionary;	ASSERT_TRUE(dictionary.CountNamberOfWords(dictionary) == 0);	auto file = "file.txt";	dictionary.ReadDictionaryFromFile(dictionary, file);	ASSERT_TRUE(dictionary.CountNamberOfWords(dictionary) > 0);}